<js>
m=[];
n=[];
p=[];
//加入替换队列
function x(a,b) {
	m.push(a);
	n.push(b);
	p.push(null);
}
function(a,b,c){
	m.push(a);
	n.push(b);
	p.push(c);
}
//clear
function c() {
	m=[];
 	n=[];	
 	p=[];
}
//excute
function e(debug){
	for(i in n) {
		result=result.replace(m[i],n[i]);
	}
	if(debug) {
		java.log(debug);
		java.log(result);
	}
	c();
}

if(/Undefined/.exec(result)){
	result= "<br>　　网站分页后第三页有时有问题不可用，速度慢的时候就会是现在这样了，网站速度快的时候就没事了。<br>　　换个时间刷新试试！！！<br>　　换个时间刷新试试！！！<br>　　换个时间刷新试试！！！<br>" + result;
}
//转换空格
x(/[ \t]+</g,'<');
x(/&nbsp;/g,' ');
//初始化分段准备
x(/[\t ]{2,2}/g,"　");
//初始化图片链接
x(/[\r\n]?[\t ]*<img .+?toimg\/data\/([^\.]+)[^>]+>[\t ]*[\r\n]?/g,"#$1#");


//去掉标签里的属性
x(/(\<|&lt;)([^\>]*?)(\s|&nbsp;)[^\>]*(\>|&gt;)/g,"<$2>");
//去掉超链接
x(/\<a\>[^\>]+\>/gi,"");
x(/\<script\>[^\>]+\>/gi,"");
//调整排版
x(/\s*(<br>)?\s*　{2,}/gi,"</p>\r\n<p>");
x(/\s*[\r\n]?\s*<div[^\>]+\>\s*[\r\n]\s*/gi,"</p>\r\n<p>");

//去掉不需要的标签
x(/[\r\n]?\<([^\/p][^\<\>]*)?\>[\r\n]?/gi,"");
x(/[\r\n]?\<\/([^p][^\<\>]*)?\>[\r\n]?/gi,"");
//乱码删除
x(/(^\s+)|(\s+)$|(\s+)[\r\n]/gi,"");
x(/(^|[\r\n])(<p>)?[^><]*(\>|&gt;)/gi,"$2");
x(/<p>\s*o([\r\n]|$)/g,'');


//开头是否包含段落截断标识
x(/^(?!(\<p\>))/i,"$1hereispagebreak");

//执行替换，不能删
e();


//测试中，下面的e();不要打开
x(/‘/g,"『");
x(/’/g,"』");
x(/“/g,"「");
x(/”/g,"」");

e("1111111111111111111111111111111111111111111111111111111111111111111111111111");
result;
</js>

